/// @title CryptoPostCards
/// @author Pelf Sollution
/// @notice CryptoPostCards contract is the main input to the this DApp


contract CryptoPostCards is Ownable, ERC721 {

    using SafeMath for uint256;

    address owner; // Contract Owner
     address charityowner;

    uint256 private startingPrice = 0.1 ether;
    bool private erc721Enabled = false;

    /// @notice Name and symbol of the non fungible token, as defined in ERC721.
    string public constant name = "CryptoPostCards";
    string public constant symbol = "CPC";

    /// @notice The contract that will return kitty metadata.
    ERC721Metadata public erc721Metadata;

    bytes4 constant InterfaceSignature_ERC165 = bytes4(keccak256("supportsInterface(bytes4)"));
    bytes4 constant InterfaceSignature_ERC721 = bytes4(keccak256("name()")) ^ 
    bytes4(keccak256("symbol()")) ^ 
    bytes4(keccak256("totalSupply()")) ^ 
    bytes4(keccak256("balanceOf(address)")) ^ 
    bytes4(keccak256("ownerOf(uint256)")) ^ 
    bytes4(keccak256("approve(address,uint256)")) ^ 
    bytes4(keccak256("transfer(address,uint256)")) ^ 
    bytes4(keccak256("transferFrom(address,address,uint256)")) ^ 
    bytes4(keccak256("tokensOfOwner(address)")) ^ 
    bytes4(keccak256("tokenMetadata(uint256,string)"));

    /// @notice Structs

    struct PostCard {
        string name;
        string imageLink;
    }

    /* struct Struct{
        string nameId;
        string lctn;      //location of Tajmahal
        address owner;    //Owner of Tajmahal
        uint price;       // price of Tajmahal in ether
    }*/

       struct Donation {
        uint256 kittyId;
        address trustAddress;
        address fosterAddress;
        uint256 trustAmount;
        uint256 fosterAmount;
    }

    PostCard[] private PostCards;
    Donation[] public donations;

    //Mapping from token ID to owner
    mapping (uint256 => address) internal tokenOwner;
    //Mapping from ID to price
    mapping (uint256 => uint256) internal tokenIdToPrice;
    //Mapping from owner to number of owned token
    mapping (address => uint256) internal ownedTokensCount;
    //Mapping from token ID to approved address
    mapping (uint256 => address) internal tokenApprovals;
    // Mapping from owner to operator approvals
    mapping (address => mapping (address => bool)) internal operatorApprovals;
    // Optional mapping for token URIs 
    mapping(uint256 => string) internal tokenURIs;
    // Charity mapping address to amont
    mapping(address => uint256) public amountAddressCharity;

    event TokenCreated(uint256 tokenId, string name, string imageLink, uint256 price, address owner);
    event TokenSold(uint256 indexed tokenId,string name,string imageLink,uint256 sellingPrice,uint256 newPrice,address indexed oldOwner,address indexed newOwner);



    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

      constructor() payable public {
        owner = msg.sender;

    }


    /// @notice Introspection interface as per ERC-165 (https://github.com/ethereum/EIPs/issues/165).
    ///  Returns true for any standardized interfaces implemented by this contract. We implement
    ///  ERC-165 (obviously!) and ERC-721.
    function supportsInterface(bytes4 _interfaceID) external view returns (bool) {
        // DEBUG ONLY
        //require((InterfaceSignature_ERC165 == 0x01ffc9a7) && (InterfaceSignature_ERC721 == 0x9a20483d));
        return ((_interfaceID == InterfaceSignature_ERC165) || (_interfaceID == InterfaceSignature_ERC721));
    }

    /// @dev Set the address of the sibling contract that tracks metadata.
    function setMetadataAddress(address _contractAddress) public onlyOwner {
        erc721Metadata = ERC721Metadata(_contractAddress);
    }

    //

    function createToken(string _name, string _imageLink, address _owner, uint256 _price) public onlyOwner {
        require(_owner != address(0));
        require(_price >= startingPrice);

        _createToken(_name,_imageLink, _owner, _price);
    }

 // OJO TESTEANDO NO SE SI DEJARLA O QUE
        function addToken(string _name, string _imageLink) public {

        _createToken(_name,_imageLink, address(this), startingPrice);
    }


    function createToken(string _name, string _imageLink) public onlyOwner {

        _createToken(_name,_imageLink, address(this), startingPrice);
    }

    function _createToken(string _name, string _imageLink, address _owner, uint256 _price) private {
        PostCard memory _PostCard = PostCard({
            name: _name,
            imageLink: _imageLink
        });
        uint256 newTokenId = PostCards.push(_PostCard) - 1;
        tokenIdToPrice[newTokenId] = _price;

        emit TokenCreated(newTokenId, _name, _imageLink, _price, _owner);

        _transfer(address(0), _owner, newTokenId);
    }

    function getToken(uint256 _tokenId) public view returns (
        string _tokenName,
        string _imageLink,
        uint256 _price,
        uint256 _nextPrice,
        address _owner
    ) {
        _tokenName = PostCards[_tokenId].name;
        _imageLink = PostCards[_tokenId].imageLink;
        _price = tokenIdToPrice[_tokenId];
        _nextPrice = nextPriceOf(_tokenId);
        _owner = tokenOwner[_tokenId];
    }

    function getAllTokens() public view returns (
        uint256[],
        uint256[],
        address[]
    ) {
        uint256 total = totalSupply();
        uint256[] memory prices = new uint256[](total);
        uint256[] memory nextPrices = new uint256[](total);
        address[] memory owners = new address[](total);

        for (uint256 i = 0; i < total; i++) {
            prices[i] = tokenIdToPrice[i];
            nextPrices[i] = nextPriceOf(i);
            owners[i] = tokenOwner[i];
        }

        return (prices, nextPrices, owners);
    }

    function tokensOf(address _owner) public view returns(uint256[]) {
        uint256 tokenCount = balanceOf(_owner);
        if (tokenCount == 0) {
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](tokenCount);
            uint256 total = totalSupply();
            uint256 resultIndex = 0;

            for (uint256 i = 0; i < total; i++) {
                if (tokenOwner[i] == _owner) {
                    result[resultIndex] = i;
                    resultIndex++;
                }
            }
            return result;
        }
    }

        function putCharityAdress(address _charityowner) public onlyOwner{

        charityowner = _charityowner;

    }



    function withdrawBalance(address _to, uint256 _amount) public onlyOwner {
        require(_amount <= address(this).balance);

        uint256 amountToWithdraw = _amount;

        if (amountToWithdraw == 0) {
            amountToWithdraw = address(this).balance;
        }

        if(_to == address(0)) {
            owner.transfer(amountToWithdraw);
        } else {
            _to.transfer(amountToWithdraw);
        }
    }

    function purchase(uint256 _tokenId) public payable {
        uint256 id = _tokenId;
        address oldOwner = oldOwnerOf(id);
        address newOwner = msg.sender;
        uint256 sellingPrice = priceOf(_tokenId);

        require(oldOwner != address(0));
        require(newOwner != address(0));
        require(oldOwner != newOwner);
        require(!_isContract(newOwner));
        require(sellingPrice > 0);
        require(msg.value >= sellingPrice);

        _transfer(oldOwner, newOwner, _tokenId);
        tokenIdToPrice[_tokenId] = nextPriceOf(_tokenId);
        emit TokenSold(
            _tokenId,
            PostCards[_tokenId].name,
            PostCards[_tokenId].imageLink,
            sellingPrice,
            priceOf(_tokenId),
            oldOwner,
            newOwner
        );

        uint256 excess = msg.value.sub(sellingPrice);
        uint256 charityCut = sellingPrice.mul(30).div(100); // 30% cut

        if (charityowner != address(this)) {
            charityowner.transfer(sellingPrice.sub(charityCut));
        }

        if (excess > 0) {
            newOwner.transfer(excess);
        }
    }

    function priceOf(uint256 _tokenId) public view returns (uint256 _price) {
        return tokenIdToPrice[_tokenId];
    }

    uint256 private increaseLimit1 = 0.02 ether;
    uint256 private increaseLimit2 = 0.5 ether;
    uint256 private increaseLimit3 = 2.0 ether;
    uint256 private increaseLimit4 = 5.0 ether;

    function nextPriceOf(uint256 _tokenId) public view returns (uint256 _nextPrice) {
        uint256 _price = priceOf(_tokenId);
        if (_price < increaseLimit1) {
            return _price.mul(200).div(95);
        } else if (_price < increaseLimit2) {
            return _price.mul(135).div(96);
        } else if (_price < increaseLimit3) {
            return _price.mul(125).div(97);
        } else if (_price < increaseLimit4) {
            return _price.mul(117).div(97);
        } else {
            return _price.mul(115).div(98);
        }
    }

    function enableERC721() public onlyOwner {
        erc721Enabled = true;
    }

    function totalSupply() public view returns (uint256 _totalSupply) {
        _totalSupply = PostCards.length;
    }

    function balanceOf(address _owner) public view returns (uint256 _balance) {
        _balance = ownedTokensCount[_owner];
    }

   function ownerOf(uint256 _tokenId) external view returns (address _owner) {
        _owner = tokenOwner[_tokenId];
    }

    function oldOwnerOf(uint256 _tokenId) public view returns (address _owner) {
        _owner = tokenOwner[_tokenId];
    }

    function approve(address _to, uint256 _tokenId) external {
        require(_owns(msg.sender, _tokenId));
        tokenApprovals[_tokenId] = _to;
        emit Approval(msg.sender, _to, _tokenId);
    }

    function transferFrom(address _from, address _to, uint256 _tokenId) external {
        require(_to != address(0));
        require(_owns(_from, _tokenId));
        require(_approved(msg.sender, _tokenId));

        _transfer(_from, _to, _tokenId);
    }

    function transfer(address _to, uint256 _tokenId) external  {
        require(_to != address(0));
        require(_owns(msg.sender, _tokenId));

        _transfer(msg.sender, _to, _tokenId);
    }

    function implementsERC721() public view returns (bool) {
        return erc721Enabled;
    }

    function takeOwnership(uint256 _tokenId) public  {
        require(_approved(msg.sender, _tokenId));
        _transfer(tokenOwner[_tokenId], msg.sender, _tokenId);
    }


    //From full ERC721 implementation 
    function _setTokenURI(uint256 _tokenId, string _uri) internal {
        require(_exists(_tokenId));
        tokenURIs[_tokenId] = _uri;
    }

    //From basic ERC721 implementation
        /**
   * @dev Returns whether the specified token exists
   * @param _tokenId uint256 ID of the token to query the existence of
   * @return whether the token exists
   */
    function _exists(uint256 _tokenId) internal view returns (bool) {
        return tokenOwner[_tokenId] != address(0);
    }



    function _owns(address _claimant, uint256 _tokenId) private view returns (bool) {
        return tokenOwner[_tokenId] == _claimant;
    }

    function _approved(address _to, uint256 _tokenId) private view returns (bool) {
        return tokenApprovals[_tokenId] == _to;
    }

    function _transfer(address _from, address _to, uint256 _tokenId) private {
        ownedTokensCount[_to]++;
        tokenOwner[_tokenId] = _to;

        if (_from != address(0)) {
            ownedTokensCount[_from]--;
            delete tokenApprovals[_tokenId];
        }

        emit Transfer(_from, _to, _tokenId);
    }

    //From AddressUtils.sol library
    function _isContract(address addr) private view returns (bool) {
        uint256 size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }
}

